var currentRuCount = Class.create();
currentRuCount.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

 getCmdbCount: function() {
		var cmdbCount = new GlideRecord('x_1166758_account_ru_definition');
            cmdbCount.addQuery('sys_id',this.getParameter("sysparm_ru_name"));
            cmdbCount.query();
	while(cmdbCount.next()){
	var count = cmdbCount.current_month_count;
		gs.info(count);
}
		return count;

    },
    type: 'currentRuCount'
});
